mapper.select:
SELECT 

FROM info_role_menu  
WHERE role_id=#{roleId} 
AND module_id=#{moduleId} 
ORDER BY sort_no

SELECT 

FROM info_role_menu  
WHERE role_id=#{roleId} 
AND menu_id=#{menuId} 
ORDER BY sort_no

mapper.insert:
INSERT INTO info_role_menu 
	(role_id,  module_id, menu_id, sort_no, create_id, create_time, update_id, update_time) 
VALUES (
	#{roleId},  #{moduleId}, #{menuId}, #{sortNo}, 
	#{createId}, #{createTime}, #{updateId}, #{updateTime}
)

mapper.select:
select 

from info_role_menu rm left JOIN func_menu fm on rm.menu_id=fm.id
where rm.role_id=#{roleId} and fm.is_delete!=1;
